            return '210A';
        case 'h':
            return '1D4BD';
        case 'i':
            return '1D4BE';
        case 'j':
            return '1D4BF';
        case 'k':
            return '1D4C0';
        case 'l':
            return '1D4C1';
        case 'm':
            return '1D4C2';
        case 'n':
            return '1D4C3';
        case 'o':
